package firstday;

public class IdentifierDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte minByte= -128;
		byte maxByte= 127;
		System.out.println("Range of byte datatype is "+ minByte + " to " + maxByte);
		
		short minShort= -32768;
		short maxShort= 32767;
		System.out.println("Range of Short datatype is "+ minShort + " to " + maxShort);
		
		int minInteger= -2147483648;
		int maxInteger= 2147483647;
		System.out.println("Range of Integer datatype is "+ minInteger + " to " + maxInteger);
		
		long minLong= -9223372036854775808l;
		long maxLong= -9223372036854775807l;
		System.out.println("Range of long datatype is "+ minLong + " to " + maxLong);
		
				
			
		
	}

}