
public class switchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='a';
		switch(ch) {
		case 'l':
			System.out.println(ch+ "for lion");
		case 't':
			System.out.println(ch+ " for tiger");
			
			default:
				System.out.println(ch + " is not the available charater");
			
		}

	}

}
